import React from 'react';
import ReactDOM from 'react-dom/client';
import './index.css';
import reportWebVitals from './reportWebVitals';
import './App.css';

const fName = 'Michal';
const lName = 'Fingert';
const date = new Date();
const hour = date.getHours();
const img1 = 'https://picsum.photos/200';

const style = {
  color: '',
};

const getText = () => {
  if (hour <= 12) {
    style.color = 'red';
    return 'Good morning';
  } else if (hour >= 12 && hour <= 18) {
    style.color = 'green';
    return 'Good afternoon';
  } else {
    style.color = 'blue';
    return 'Good evening';
  }
};

const root = ReactDOM.createRoot(document.getElementById('root'));
root.render(
  <div className="App">
    <h1 className="header" contentEditable="true" spellCheck="false">
      Created by {fName} {lName}
    </h1>
    <p style={style}> {getText()}</p>
  </div>,
);
reportWebVitals();
