import Card from './components/Card';
import Header from './components/Header';
import Footer from './components/Footer';
import InputArea from './components/InputArea';
import notes from './notes';
import './App.css';
import React, { useState } from 'react';

function App() {
  const [Anotes, setNotes] = useState(notes);
  const [keyCounter, setKeyCounter] = useState(notes.length);
  function deleteNote() {
    setNotes((prevNotes) => {
      prevNotes = prevNotes.filter((note) => {
        return note.key === note;
      });
      return [...prevNotes];
    });
  }
  return (
    <div className="container">
      <Header title="Keeper" />
      <InputArea
        stateFunc={setNotes}
        setKey={setKeyCounter}
        keyCounter={keyCounter}
      />
      <div className="cards-container">
        {Anotes.map((note) => {
          return (
            <div className="note">
              <Card key={note.key} title={note.title} content={note.content} />
              <button onClick={deleteNote(note.key)}> delete</button>
            </div>
          );
        })}
      </div>
      <Footer />
    </div>
  );
}

export default App;
